import {ADD_CONTACT,DELETE_CONTACT,SET_CURRENT,CLEAR_CONTACTS,CLEAR_CURRENT,UPDATE_CONTACT,FILTER_CONTACTS,CLEAR_FILTER} from '../types';

export default (state , action) =>{
    switch(action.type){
        default:
            return state;
        case ADD_CONTACT:
            return{
                ...state,
                contacts:[...state.contacts, action.payload]
            }
    }
}
