import React,{ useState,useContext,useEffect} from 'react';
import contactContext from '../../context/contact/contactContext';

const ContactForm = () => {
    const ContactContext =useContext(contactContext);
    const { addContact, current}= ContactContext;
    useEffect(()=>{
        if(current!==null){
            setContact(current);
        }else{setContact({
            name: '',
            email: '',
            phone: '',
            type: 'personal'
        })}
    },[ContactContext,current]);        //these are dependencies i.e we want to use useEffect only when they are changed
    const[contact,setContact]=useState({
        name: '',
        email: '',
        phone: '',
        type: 'personal'
    })
    const{name,email,phone,type}=contact;
    const onChange = (e) =>{
        setContact({...contact, [e.target.name]: e.target.value})
    }
    const onSubmit = (e) =>{
        e.preventDefault();
        addContact(contact);
        setContact({
            name: '',
            email: '',
            phone: '',
            type: 'personal'
        })
    }
    return (
        <form onSubmit={onSubmit}>
            <h2 className='text-primary'>{current?'Edit Contact':'Add Contact'}</h2>
            <input type='text' placeholder='Name' name='name' value={name} onChange={onChange} />
            <input type='email' placeholder='Email' name='email' value={email} onChange={onChange} />
            <input type='text' placeholder='Phone' name='phone' value={phone} onChange={onChange} />
            <h5>Contact Type</h5>
            <input type='radio'onChange={onChange} name='type' value='personal' checked={type==='personal'}/> Personal{' '}
            <input type='radio'onChange={onChange} name='type' value='professional' checked={type==='professional'}/> Professional{' '}
            <div>
                <input type='submit' value={current?'Edit Contact':'Add Contact'} className='btn btn-primary btn-block' /> 
            </div>
        </form>
    )
}

export default ContactForm
